"use strict";

const mongoose = require('mongoose');
const validate = require('mongoose-validator');

const Schema = mongoose.Schema;
const ObjectId = Schema.Types.ObjectId;

const titleValidator = [
  validate({
    validator: 'isLength',
    arguments: [3, 20],
    message: 'Name should be between {ARGS[0]} and {ARGS[1]} characters'
  })
];

const bankaccounts_schema = Schema({
  id: Number,
  number : String,
  owner: String,
  branch_id: {
    type: ObjectId,
    ref: 'branches'
  },
  creator_id: {
    type: ObjectId,
    ref: 'users'
  },
  comment: String,
  disable: Boolean
});


module.exports = mongoose.model('bankaccounts', bankaccounts_schema);
