var mongoose = require('mongoose');
var Schema = mongoose.Schema;
var ObjectId = Schema.Types.ObjectId;

var branch_schema = Schema({
  //
  id: Number,
  title: String,
  bank_id: {
    type: ObjectId,
    ref: 'banks'
  },

  creator_id: {
    type: ObjectId,
    ref: 'users'
  }
});


module.exports = mongoose.model('branches', branch_schema);
