var mongoose = require('mongoose');
var Schema = mongoose.Schema;
var ObjectId = Schema.Types.ObjectId;

var by_schema = Schema({
  //
  title: String,

  person_id : {
    type : ObjectId,
    ref : 'persons'
  },

  creator_id: {
    type: ObjectId,
    ref: 'users'
  }

});


module.exports = mongoose.model('bys', by_schema);
