var mongoose = require('mongoose');
var Schema = mongoose.Schema;
var ObjectId = Schema.Types.ObjectId;

var costs_schema = Schema({
  //
  id : Number,
  title: String,

  creator_id: {
    type: ObjectId,
    ref: 'users'
  },

  comments: String
});


module.exports = mongoose.model('costs', costs_schema);
