var mongoose = require('mongoose');
var Schema = mongoose.Schema;
var ObjectId = Schema.Types.ObjectId;

var g_persons_schema = Schema({
  //
  title: String,
  //
  credit_limit: Number,
  // _id of the user add this user
  creator_id: {
    type: ObjectId,
    ref: 'users'
  },
  // comment about this persons, every thing needed..
  comments: String,

  // if true , user is disable
  disable: Boolean

});


module.exports = mongoose.model('g_persons', g_persons_schema);
