var mongoose = require('mongoose');
var Schema = mongoose.Schema;
var ObjectId = Schema.Types.ObjectId;

var g_products_schema = Schema({

  title: String,

  // _id of the user add this user
  creator_id: {
    type: ObjectId,
    ref: 'users'
  },
  // comment about this g_products, every thing needed..
  comments: String,

  // if true , group product is disable
  disable: Boolean

});


module.exports = mongoose.model('g_products', g_products_schema);
