var mongoose = require('mongoose');
var Schema = mongoose.Schema;
var ObjectId = Schema.Types.ObjectId;

// these schema is for users, every one want's to use this app is a user
// and there is not diffrece between students and employees ,everyone!
// tasks are deffrence of any job.
var g_users_schema = Schema({
    //
   title: String,
   name: String,
   //null
   credit_limit: Number,
   // tasks are link to task collection in db and show what is this users
   // permissions to access parts of app.

   // every user can have infinity tasks!
   permissions: [
      String
   ],

   // _id of the user add this user
   creator_id: {
      type: ObjectId,
      ref: 'users'
   },

   // comment about this users, every thing needed..
   comments: String,

   // if true , user is disable
   disable: Boolean

});


module.exports = mongoose.model('g_users', g_users_schema);
