"use strict";

const mongoose = require('mongoose');
const Schema = mongoose.Schema;
const ObjectId = Schema.Types.ObjectId;

const incomes_schema = Schema({
  //
  id: Number,
  title: String,

  creator_id: {
    type: ObjectId,
    ref: 'users'
  },

  comments: String
});


module.exports = mongoose.model('incomes', incomes_schema);
