//

var mongoose = require('mongoose');
var Schema = mongoose.Schema;
var ObjectId = Schema.Types.ObjectId;

var print_bankdoc_schema = Schema({
   name: String,
   title: String,
   fields: [{

      // this name is name that saved in db
      name: String,

      // title shows to users of thing filed
      title: String,

      // type of field
      type: {
         type: String,
         enum: ["checkbox", "text", "file", "combo", "multiple_combo", "email", "number"]
      },

      // if combo box we need it's defult values for
      values: [],

      // min and max is for numbers
      min: Number,
      max: Number,
      required: Boolean,
      place_holder: String,

      // validate with this
      rejex: String,
      help: String
   }],
   _creator: {
      type: ObjectId,
      ref: 'users'
   }
});

module.exports = mongoose.model('print_bankdoc', print_bankdoc_schema);
