var mongoose = require('mongoose');
var Schema = mongoose.Schema;
var ObjectId = Schema.Types.ObjectId;

var units_schema = Schema({
  //
  title: String,

  // _id of the user add this unit
  creator_id: {
    type: ObjectId,
    ref: 'users'
  }

});


module.exports = mongoose.model('units', units_schema);
