var db = require('db');

module.exports = function remove(bank_id, cb) {

  cb = cb || function() {};

  if (!bank_id) {
    cb({
      status: 400,
      message: "low args"
    });
  } else {

    // TODO check bankaccount in not invoice
    db.banks.remove({
      _id: bank_id
    }).lean().exec(function(err, res) {
      if (err) {
        cb({
          status: 500,
          error_obj: err
        });
      } else if (res) {
        if (res) {
          cb(null, true);
          // removed
        }
      }
    });
  }
}
