var db = require('db');

module.exports = function (invoice_id, cb) {

  cb = cb || function() {};
  if (!invoice_id) {
    cb({
      status: 400,
      message: "low args"
    });
  } else {

    // console.log('as');
    // TODO check invoice in not invoice
    db.operations.remove({
      _id: invoice_id
    }).lean().exec(function(err, res) {
      if (err) {
        cb({
          status: 500,
          error_obj: err
        });
      } else if (res) {
        // remove
        cb(null, true);
      }
    });
  }
}
