function code(cb) {

  cb = cb || function() {};

  var now = new Date();
  var y = String(now.getFullYear()).substring(2);
  var m = now.getMonth() + 1;
  var d = now.getDate();

  if (m < 10) m = '0' + String(m);
  if (d < 10) d = '0' + String(d)

  cb('2' + (y + m + d).split('').reverse().join(''));

}

module.exports = code;
