var db = require('db');

module.exports = function remove_person(person_id, cb) {

  cb = cb || function() {};

  if (!person_id) {
    cb({
      status: 400,
      message: "low args"
    });
  } else {

    // TODO check person in not invoice
    db.persons.remove({
      _id: person_id
    }).lean().exec(function(err, res) {
      if (err) {
        cb({
          status: 500,
          error_obj: err
        });
      } else if (res) {
        db.operations.remove({
          person_id: person_id,
          type: 'first_period'
        }).exec(function(err, _r) {
          if (err) throw err;
          if (_r) {
            cb(null, true);
            // removed
          }
        })
      }
    });
  }
}
