var db = require('db');

module.exports = function(transferId, cb) {
  cb = cb || function() {};
  if (!transferId) {
    cb({
      status: 400,
      message: "low args"
    });
  } else {
    db.operations.remove({
      id: transferId.id
    }).lean().exec(function(err, res) {
      if (err) {
        cb({
          status: 500,
          error_obj: err
        });
      } else if (res) {
        // remove
        cb(null, true);
      }
    });

  }
}
